import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Livro } from '../livro.model';
import { LivroService } from '../livro.service';

@Component({
  selector: 'app-livro-remove',
  templateUrl: './livro-remove.component.html',
  styleUrls: ['./livro-remove.component.css']
})
export class LivroRemoveComponent implements OnInit{

  livro!:Livro
  constructor(private bookService:LivroService,private router:Router,private activeRouter:ActivatedRoute){}

  ngOnInit(): void {
    const id = this.activeRouter.snapshot.paramMap.get('id');
    this.bookService.readById(id!).subscribe(livro => {
      this.livro = livro;
    })
  }

  deleteBook():void{
    this.bookService.delete(this.livro.id!).subscribe(() => {
      this.router.navigate([''])
    })
  }

  cancel(): void{
    this.router.navigate([''])
  }



}
<div class="container">
    <div class="pb-2 mt-4 mb-4 border-bottom">
        <h2>deletar livro</h2>
    </div>

    <div class="col-sm-6">
        <form>

            <div class="form-group">
                <label for="titulo">titulo</label>
                <input type="text" class="form-control" [(ngModel)]="livro.titulo" name="titulo" readonly>
            </div>

            <button type="submit" class="btn btn-danger" (click)="deleteBook()">deletar</button>
            <button type="button" class="btn btn-warning" (click)="cancel()">cancelar</button>
        </form>
    </div>
</div>

