import { Component } from '@angular/core';
import { Subject } from 'rxjs';
import { Product } from '../product-model';
import { ProductService } from '../product.service';

@Component({
  selector: 'app-products',
  templateUrl: './products.component.html',
  styleUrls: ['./products.component.css']
})
export class ProductsComponent {
  dtOptions:DataTables.Settings = {}

  dtTrigger: Subject<any> = new Subject<any>();
  product!:Product[]

  constructor(private productService:ProductService) { }

  ngOnInit(): void {
    this.dtOptions = {
      pagingType: 'full_numbers',
      pageLength: 9
    };

    this.productService.read().subscribe(product => {
      this.product = product;
      this.dtTrigger.next(product);
    })
  }

  ngOnDestroy(): void {
    this.dtTrigger.unsubscribe();
  }
}
<div class="container">
    <div class="row product-read ">
        <div class="page-header">
            <h2>tabela de produtos</h2>
        </div>
            <table id="tabela"datatable [dtOptions]= "dtOptions" [dtTrigger]="dtTrigger" class="table table-bordered row-border hover">
                <thead>
                    <tr>
                        <th>id</th>
                        <th>name</th>
                        <th>price</th>
                        <th>edit</th>
                        <th>remove</th>
                    </tr>
                </thead>

                <tbody>
                    <tr *ngFor="let products of product">
                        <td>{{ products.id }}</td>
                        <td>{{ products.name }}</td>
                        <td>{{ products.price | currency: 'BRL'}}</td>
                        <td class="icons edit">
                            <a routerLink="/products-update/{{ products.id }}">
                                <i class="glyphicon glyphicon-pencil"></i>
                            </a>
                        </td>
                        <td class="icons remove">
                            <a routerLink="/products-delete/{{ products.id }}">
                                <i class="glyphicon glyphicon-trash"></i>
                            </a>
                        </td>
                    </tr>
                </tbody>
            </table>


    </div>
</div>
